 */
abstract class BasePollOption extends Record
{
    public function setTableDefinition()
    {
        $this->setTableName('poll_option');
        $this->hasColumn('poll_id', 'integer', 9, array(
             'type' => 'integer',
             'length' => '9',
             ));
        $this->hasColumn('name', 'string', 255, array(
             'type' => 'string',
             'length' => '255',
             ));
    }

    public function setUp()
    {
        parent::setUp();
        $this->hasMany('User as Polleds', array(
             'refClass' => 'PollOptionUser',
             'local' => 'poll_option_id',
             'foreign' => 'account_id'));

        $this->hasOne('Poll', array(
             'local' => 'poll_id',
             'foreign' => 'id'));

        $this->hasMany('PollOptionUser', array(
             'local' => 'id',
             'foreign' => 'poll_option_id'));
    }
}