abstract class BaseEvent extends Record
{
    public function setTableDefinition()
    {
        $this->setTableName('event');
        $this->hasColumn('name', 'varchar', 255, array(
             'type' => 'varchar',
             'length' => '255',
             ));
        $this->hasColumn('period', 'datetime', null, array(
             'type' => 'datetime',
             ));
    }

    public function setUp()
    {
        parent::setUp();
        $this->hasMany('Character as Participants', array(
             'refClass' => 'EventParticipant',
             'local' => 'event_id',
             'foreign' => 'character_id'));
    }
}